// Hàm trả về chuỗi mới có thêm '*' ở đầu
char* add_marker(const char *input) {
    if (input == NULL) return NULL;

    size_t len = strlen(input);
    char *result = malloc(len + 2); // +1 cho '*', +1 cho '\0'
    if (result == NULL) return NULL;

    result[0] = '*';
    strcpy(result + 1,   input);

    return result;
}

// Hàm trả về chuỗi mới có thêm '.' ở đầu
char* add_marker_dot(const char *input) {
    if (input == NULL) return NULL;

    size_t len = strlen(input);
    char *result = malloc(len + 2); // +1 cho '*', +1 cho '\0'
    if (result == NULL) return NULL;

    result[0] = '.';
    strcpy(result + 1,   input);

    return result;
}


// Hàm trả về con trỏ tới chuỗi gồm ký tự thứ 9 và 10 (index 8 và 9)
char* get_substring_9_10(const char *input) {
    if (!input || strlen(input) < 10) {
        return strdup("");  // trả về chuỗi rỗng, cần free
    }
    char* result = malloc(3);  // 2 ký tự + null-terminator
    if (!result) return NULL;
    result[0] = input[8];
    result[1] = input[9];
    result[2] = '\0';
    return result;
}


char* concat_three_strings(const char* s1, const char* s2, const char* s3) {
    char* result = malloc(256);  // cần free sau khi dùng
    if (!result) return NULL;
    snprintf(result, 256, "%s%s%s", s1 ? s1 : "", s2 ? s2 : "", s3 ? s3 : "");
    return result;
}

