const char* get_first_token(const char* input) {
    static char result[64];  // đủ lớn để chứa kết quả
    size_t i = 0;
    size_t j = 0;

    // Bỏ qua 2 ký tự đầu tiên
    input += 2;

    // Lặp qua từng ký tự cho đến khi gặp dấu cách hoặc kết thúc chuỗi
    while (input[i] != '\0' && input[i] != ' ' && j < sizeof(result) - 1) {
        result[j++] = input[i++];
    }

    result[j] = '\0';  // kết thúc chuỗi
    return result;
}
